import React, { Component } from 'react';
import '../Style/Dishes.css';

class Dishes extends Component {
  constructor() {
    super();

    this.state = {
      Dishes: []
    }
  }

  componentDidMount(){
    fetch('/api/Dishes')
      .then(res => res.json())
      .then(Dishes => this.setState({Dishes}, () => console.log('Food fetched', Dishes)));
  }

  formatDate(date){
    var newDate = new Date(date);
    var dateUs = newDate.toLocaleDateString("en-US");
    return (dateUs);
  }

  expirationStatus(date){
    var d1 = new Date();
    var d2 = new Date(date);
    var diff = Math.round((d2-d1)/86400000);
    if(diff <= 0){
      var expired = "Expired";
      return expired;
    }
    else
    var notExpired = "notExpired";
    return notExpired;
  }

 
  render() {
    return(
      <div className="foods">
        <h2>Inventory</h2>
        <table>
          <tbody>
            <tr className="column-title">
              <th>Food Name</th>
              <th>Food Description</th>
              <th>Quantity</th>
              <th>Price Per Item</th>
              <th>Purchased Date</th>
              <th>Expiration Date</th>
              <th>Expiration Status</th>
              <th>Total Price of Goods</th>
            </tr>
            {this.state.Dishes.map(Dishes => 
              <tr key={Dishes._id}>
                <th> {Dishes.name} </th>
                <th> {Dishes.description} </th>
                <th> {Dishes.quantity} </th>
                <th> ${Dishes.price} </th>
                <th> {this.formatDate(Dishes.purchased)} </th>
                <th> {this.formatDate(Dishes.expires)} </th>
                <th> {this.expirationStatus(Dishes.expires)}</th>
                <th> ${Math.round(Dishes.quantity * Dishes.price)} </th>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Dishes;
