import React, { Component } from 'react';
import '../Style/Dishes.css';

class Dishes extends Component {
  constructor() {
    super();

    this.state = {
      Dishes: []
    }
  }

  componentDidMount(){
    fetch('/api/dishes')
      .then(res => res.json())
      .then(Dishes => this.setState({Dishes}, () => console.log('Food fetched', Dishes)));
  }

  formatDate(date){
    var newDate = new Date(date);
    var dateUs = newDate.toLocaleDateString("en-US");
    return (dateUs);
  }

  expirationStatus(date){
    var d1 = new Date();
    var d2 = new Date(date);
    var diff = Math.round((d2-d1)/86400000);
    if(diff <= 0){
      var expired = "Expired";
      return expired;
    }
    else
    var notExpired = "notExpired";
    return notExpired;
  }

 
  render() {
    return(
      <div className="foods">
        <h2>Inventory</h2>
        <table>
          <tbody>
            <tr className="column-title">
              <th>Dish Name</th>
              <th>Dish Description</th>
              <th>Cost to Make Dish</th>
              <th>Sale Price of Dish</th>
              <th>Number of Dishes Sold</th>
              <th>Total Profit</th>
            </tr>
            {this.state.Dishes.map(Dishes => 
              <tr key={Dishes._id}>
                <th> {Dishes.name} </th>
                <th> {Dishes.description} </th>
                <th> {Dishes.quantity} </th>
                <th> ${Dishes.price} </th>
                <th> ${Math.round(Dishes.quantity * Dishes.price)} </th>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Dishes;
