import React, { Component } from 'react';
import '../../Style/Dishes.css';

class Dishes extends Component {
  constructor() {
    super();

    this.state = {
      Dishes: []
    }
  }

  componentDidMount(){
    fetch('/api/dishes')
      .then(res => res.json())
      .then(Dishes => this.setState({Dishes}, () => console.log('Dishes fetched', Dishes)));
  }

  handleClick(userId){
    const requestOptions = {
      method: 'DELETE'
    };
    // Note: I'm using arrow functions inside the `.fetch()` method.
    // This makes it so you don't have to bind component functions like `setState`
    // to the component.
    fetch("/api/dishes" + userId, requestOptions).then((response) => {
      return JSON.stringify(response);
    }).then((result) => {
      console.log(result);
    });
  }
  render() {
    return(
      <div className="foods">
        <br/>
        <h2>Dishes</h2>
        <table>
          <tbody>
            <tr className="column-title">
              <th>Dish Name</th>
              <th>Dish Description</th>
              <th>Cost to Make Dish</th>
              <th>Sale Price of Dish</th>
              <th>Number of Dishes Sold</th>
              <th>Dish Profit</th>
            </tr>
            {this.state.Dishes.map(Dishes => 
              <tr key={Dishes._id}>
                <th> {Dishes.name} </th>
                <th> {Dishes.description} </th>
                <th> ${Dishes.cost} </th>
                <th> ${Dishes.price} </th>
                <th> {Dishes.quantity} </th>
                <th> ${Math.round((Dishes.quantity * Dishes.price) - (Dishes.quantity * Dishes.cost))} </th>
                <th><button onClick={() => { this.handleClick(Dishes._id) }} className="deletebtn">Delete</button></th>
                <th><button className="editbtn">Edit</button></th>
              </tr>
            )}

          </tbody>
        </table>
        <a href="/Inventory/AddDish"> Add Dish </a>
      </div>
    );
  }
}

export default Dishes;
